import { sp, Web, IField, IFieldInfo, IViewInfo } from "@pnp/sp/presets/all";
let spWeb = null;
export default class SpTableData extends React.Component<ISpTableDataProps, ISpTableState> {
  public constructor(props: any) {
    super(props);
    sp.setup({
      ie11: true,
      spfxContext: this.context
    });
    this.state = {
      Tools: [],
      Fields: [],
      spListViewInfo:null
    }
    this.getListItems = this.getListItems.bind(this);
    this.getViewQuery = this.getViewQuery.bind(this);
    this.getData = this.getData.bind(this);
    spWeb = Web(this.props.spContext.pageContext.web.absoluteUrl);
    //spWeb = Web(sp.web.toUrl())
  }
  public componentWillMount() {
    let context = this;
    this.getViewQuery(this.props).then(
      function (query) {
        context.getData(context);
      }
    )
  }
  public componentWillReceiveProps(props) {
    var context = this;
    this.getViewQuery(props).then(function (query) {
      context.getData(context);
    });
  }

  public getData(context) {
    context.getListItems(context.props).then(function (spitems) {
      let items = [];
      spitems.forEach(element => {
        let row = {};
        //debugger;
        context.state.Fields.map((field: IFieldInfo) => {
          if (field.TypeAsString === "Lookup") {
            row[field.InternalName] = element[field.InternalName].Title;
          }
          else {
            row[field.InternalName] = element[field.InternalName]
          }
        });
        items.push(row);
      });
      console.log("thisssssssss",items);
      context.setState(
        {
          Tools: items
        })
    })
  }

  public async getListItems(props: any) {
    let strFields: string[] = []; //this.state.Fields;
    let expandCols: string[] = [];
    /*this.state.Fields.map(elm => {
      if (elm === "Country") {
        strFields.push(elm + "/Title");
        expandCols.push(elm);
      }
      else {
        strFields.push(elm);
      }
    })*/
    this.state.Fields.map((elm: IFieldInfo) => {
      if (elm.TypeAsString === "Lookup") {
        strFields.push(elm.InternalName + "/Title");
        expandCols.push(elm.InternalName);
      }
      else {
        strFields.push(elm.InternalName);
      }
    })
    let result = await spWeb.lists.getById(props.list).items.select(...strFields).expand(...expandCols).get();
    console.log("data resultttttttttttt", result)
    return result;
  }
  public async getFieldsInfo(fieldColl: string[], props: any) {
    let fieldInfoColl: IFieldInfo[] = await Promise.all(fieldColl.map(async (currfield) => {
      let currfieldInfo: IFieldInfo = await spWeb.lists.getById(props.list).fields.getByInternalNameOrTitle(currfield)();
      console.log("current field infoooooo", currfieldInfo);
      return currfieldInfo;
    }));
    return fieldInfoColl;
  }
  public async getViewQuery(props: any) {
    //sp.web.lists.getById("").views.getById("");
    let spViewInfo = await spWeb.lists.getById(props.list).views.getById(props.listview).get();
    let result = await spWeb.lists.getById(props.list).views
      .getById(props.listview).fields.get();
    let lstFieldsWithInfo: IFieldInfo[] = null;
    await Promise.resolve(this.getFieldsInfo(result.Items, props)).then(function (response: IFieldInfo[]) {
      console.log("responseeeeee", response);
      lstFieldsWithInfo = response
    });
    console.log("##################", result);
    this.setState(
      {
        Fields: lstFieldsWithInfo, spListViewInfo:spViewInfo
      })
    return result;
  }
  public render(): React.ReactElement<ISpTableDataProps> {
    if (this.state.Tools.length > 0) {
      let columns = [];

      return (
        <div style={{ maxWidth: "100%" }}>
             <MaterialTable
            columns={
              this.state.Fields.map((element: any) => {
                switch (element.TypeAsString) {
                  case "Note":
                    return (
                      {
                        title: element.Title, field: element.InternalName,
                        render: props =>
                          <div dangerouslySetInnerHTML={{ __html: props[element.InternalName] }}></div>
                      }
                    )
                  case "Choice":
                    let objVal = {};
                    element.Choices.map((elm) => {
                      objVal[elm] = elm
                    })
                    return ({ title: element.Title, field: element.InternalName, lookup: { ...objVal } })
                  default:
                    return (
                      { title: element.Title, field: element.InternalName }
                    )
                }
              })
            }
            data={this.state.Tools}
            title={this.state.spListViewInfo.Title}
            style={{height: "50", width: "10"}}
            options={{
              filtering: true             
            }}
          />
        </div>
      );
    }
    else
      return (
        <div>No Data</div>
      )
  }
}


