class FilterInput extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      name: this.props.name,
      value: "",
      placeholder: "LIKE",
      filterOptions: this.props.filterOptions,
      filterOpen: false,
      anchorEl: null
    };

    this.timeout = 0;
  }

  handleFilterClick = event => {
    this.setState({ anchorEl: event.currentTarget });
  };

  handleClose = option => {
    if (typeof option === "string") {
